import './App.css';
import React, {Component} from "react"
import flinstone from "./imgs/flinstone.png";
import homer from "./imgs/homer.jpg";
import patrick from "./imgs/patrick.jpeg";
import peppa from "./imgs/peppapig.webp";
import pikachu from "./imgs/pikachu.jpeg";
import buzz from "./imgs/buzz.jpg";
import mickey from "./imgs/mickey.jpeg";
import stewie from "./imgs/stewie.png";
import tom from "./imgs/tom.jpg";
import eye from "./imgs/eye.avif";
import schulz from "./imgs/schulz.jpg";
import phineas from "./imgs/phineas.jpeg";
import uniqid from "uniqid";


class App extends Component {
  constructor() {
    super()
    this.state = {
      score: 0,
      bestScore: 0,
      cards: [
        {title: "Flinstone", src: flinstone, clicks: 1, id:uniqid()},
        {title: "Homer", src: homer, clicks: Math.floor(Math.random() * 11), id:uniqid()},
        {title: "patrick", src: patrick, clicks: Math.floor(Math.random() * 11), id:uniqid()},
        {title: "peppapig", src: peppa, clicks: Math.floor(Math.random() * 11), id:uniqid()},
        {title: "pikachu", src: pikachu, clicks: Math.floor(Math.random() * 11), id:uniqid()},
        {title: "Buzz", src: buzz, clicks: Math.floor(Math.random() * 11), id:uniqid()},
        {title: "Mickey", src: mickey, clicks: Math.floor(Math.random() * 11), id:uniqid()},
        {title: "Stewie", src: stewie, clicks: Math.floor(Math.random() * 11), id:uniqid()},
        {title: "Tom", src: tom, clicks: Math.floor(Math.random() * 11), id:uniqid()},
        {title: "Medieval", src: eye, clicks: Math.floor(Math.random() * 11), id:uniqid()},
        {title: "Schulz", src: schulz, clicks: Math.floor(Math.random() * 11), id:uniqid()},
        {title: "Phineas", src: phineas, clicks: Math.floor(Math.random() * 11), id:uniqid()},
      ],
      clicked: [],
      scores: []
    }
    this.click = this.click.bind(this);
  }

  componentDidMount() {

  }


  click(e) {

    //collects the id(title) of the clicked element and sends it to the clicked array
    let id = e.target.id;
    this.setState({
      clicked: this.state.clicked.concat(id),
    })


    if(this.state.clicked.indexOf(e.target.id) === -1) {
       this.setState({
        score: this.state.score + 1,
       })

    }

    else {


      if(this.state.clicked.length > this.state.bestScore) {
      this.setState({
        bestScore: this.state.clicked.length,
      })
    }

      this.setState({
        score : 0,
        clicked: [],
      })
    }
  }


  render() {

  return (
  <div>


    <div className='head'>
    <h1>Wizards Memory Game</h1>
    <h4>*Earn points by clicking on a picture, but don't click more than once*</h4>
    </div>

    <div className='score'>
      <p>Score: {this.state.score}</p>
      <p>Best score: {this.state.bestScore}</p>
    </div>

   <div className='game'>

    {this.state.cards.map((item) => (
      <div key={item.id} id={item.title} className="card" onClick={this.click} >
      <img src={item.src} alt="cartoon"/>
      <h1>{item.title}</h1>
     </div>
    ))}

    </div>

  </div>

  )
}


  }


export default App;
