import './App.css';
import React, {Component} from "react"

let cards = [
  {title: "Flinstone", sr0c: "./imgs/flinstone.png"},
  {title: "Homer", src: "./imgs/homer.jng"},
  {title: "patrick", src: "./imgs/patrick.jpeg"},
  {title: "peppapig", src: "./imgs/peppapig.webp"},
  {title: "pikachu", src: "./imgs/pikachu.jpg"},
]

class App extends Component {
  constructor() {
    super()
    this.state = {
      score: 0,
      bestScore: 0
    }
  }

  render() {
  return (
  <div>

    <div className='head'>
    <h1>Wizards Memory Game</h1>
    <h4>*Earn points by clicking on a picture, but don't click more than once*</h4>
    </div>

    <div className='score'>
      <p>Score: {this.state.score}</p>
      <p>Best score: {this.state.bestScore}</p>
    </div>

  <div className='game'>
     <div class="card">
      <img src={cards[0].src}></img>
      <h>{cards[0].title}</h>
     </div>
  </div>

  </div>

  )

}
}

export default App;
