import './App.css';
import React, {Component} from "react"
import Play from "./play.js"


class App extends Component {
  constructor() {
    super()
    this.state = {
      score: 0,
      bestScore: 0,
      cards : [
        {title: "Flinstone", src: "flinstone.png", clicks:0},
        {title: "Homer", src: "./imgs/homer.jng", clicks:0},
        {title: "patrick", src: "./imgs/patrick.jpeg", clicks:0},
        {title: "peppapig", src: "./imgs/peppapig.webp", clicks:0},
        {title: "pikachu", src: "./imgs/pikachu.jpg", clicks:0},
        {title: "Buzz", src: "./imgs/buzz.jpg", clicks:0},
        {title: "Mickey", src: "./imgs/mickey.jpeg", clicks:0},
        {title: "Stewie", src: "./imgs/stewi.png", clicks:0},
        {title: "Tom", src: "./imgs/tom.jpg", clicks:0},
        {title: "Medieval", src: "./imgs/eye.avif", clicks:0},
        {title: "Schulz", src: "./imgs/schulz.jpg", clicks:0},
        {title: "Phineas", src: "./imgs/phineas.jpeg", clicks:0},
      ]
    }
  }

  render() {
  return (
  <div>
    <Play characters = {this.state.cards} />

    <div className='head'>
    <h1>Wizards Memory Game</h1>
    <h4>*Earn points by clicking on a picture, but don't click more than once*</h4>
    </div>

    <div className='score'>
      <p>Score: {this.state.score}</p>
      <p>Best score: {this.state.bestScore}</p>
    </div>

  </div>

  )

}
}

export default App;
